void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( u+u - R ) / R.y; // floor(R/4.)*4.: hack to avoid discont fwidth(atan)
    float p = 6./R.y,
          l = length(U), a = -atan(U.x,U.y),      // PI/2 tilt
          v = log(l)/(4.*log(1.618)) - a/6.283;   // 1.618 is Golden number
          // solve for l = pow(1.618, a*4./6.283)

    O = vec4( smoothstep(0.,1.5, abs(fract(v)-.5) / fwidth(v) ) );
}
