void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Simplified and explained version of the spiral effect
    /*
	float _AngularSpeed = 7.0; //<- speed the vortex spins

    vec2 pos = fragCoord/iResolution.xy - 0.5; // <- position pivoted to center of the screen

	float distanceToCenter = sqrt(pos.x*pos.x+pos.y*pos.y); // <- distance to center of the screen

    float angularFactor = ( distanceToCenter - iTime ) * _AngularSpeed; //<- return a angular factor based on how close to the center you are and time*speed

	float value = pos.x * cos(angularFactor) - pos.y * sin(angularFactor); // <- spiral function that uses the angular factor to define if the pixel is white or black

	fragColor = vec4(value, value, value, value);
	*/

    float _AngularSpeed = 7.0;
    float _Zoom = 0.03;
    float _AlphaThreshold = 0.10;
    float _AlphaContrast = 1.50;
    float _Thickness = 1.5;
    float _Intencity = 1.0;
    float _Resolution = 0.25;

    // uncomment this for Tidepod vortex
    /*
    _AngularSpeed = 2.0;
    _AlphaContrast = 15.50;
    _Thickness = 0.0005; 
    _Resolution = 1.25;
	*/

    vec4 _Cor = vec4(1.0,0.15,0.15,0.0);
    vec4 _CorPulso = vec4(0.5,0.2,1.0,0.0);
    vec4 _CorFundo = vec4(0.15,0.05,0.45,0.0);

    vec2 uv = fragCoord/iResolution.xy - 0.5;


	float i = pow(uv.x*uv.x+uv.y*uv.y,_Resolution);

	_AngularSpeed *= sqrt(i/_Zoom) - iTime;

	float x = uv.x * cos(_AngularSpeed) - uv.y * sin(_AngularSpeed);	

	float mask = (  tanh(  (  (  (i-_AlphaThreshold) *  (-1.0)+0.5)-0.5)*_AlphaContrast)*0.5+0.5);


	float vortex =  (((x+0.005)/i+_Thickness)*_Intencity);

    vec4 cor = mix(_Cor,_CorPulso,pow(i,sin(iTime*5.0)*0.5+0.5));


    fragColor = mix(_CorFundo, cor, mask * vortex);

}
