//based on https://www.shadertoy.com/view/Xts3RB

#define SPEED 0.15
#define DURATION 10.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = abs(mod(iTime*SPEED,DURATION*2.)-DURATION);


    vec4 p=vec4(fragCoord,0.,1.)/iResolution.x-.5,r=p-p,q=r;p.y+=0.225;
    q.zw-=t+0.5;

    for (float i=1.; i>0.; i-=.01) {

        float d=0.,s=1.;

        for (int j = 0; j < 6; j++)
            r=min(r=abs(mod(q*s+1.,1.+t)-1.),r.yzxw),
            d=max(d,(.3-length(r*0.95)*.3)/s),
            s*=3.1;

        q+=p*d;

        fragColor = p-p+i;

        if(d<1e-5) break;
    }
}
