void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 pv = vec2( atan(uv.x, uv.y), length(uv) );
    pv.y = log2(pv.y)-iTime;
    //pv.y = exp2(pv.y)+iTime; // succ

    vec3 col = 
        	(texture(iChannel0, pv*.125).xyz-.5)+
        .67*(texture(iChannel0, pv* .25).xyz-.5)+
        .44*(texture(iChannel0, pv*  .5).xyz-.5)+
         .3*(texture(iChannel0, pv     ).xyz-.5)+
         .2*(texture(iChannel0, pv*  2.).xyz-.5)+
        .13*(texture(iChannel0, pv*  4.).xyz-.5);

    fragColor = vec4(col*.5+.5, 1);
}
