void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	if(uv.x <= iMouse.x / iResolution.x)
    	fragColor = texture(iChannel1, uv);
    else
    	fragColor = texture(iChannel0, uv);
}

const float R = 12.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xy;
	vec3 sum_c = texture(iChannel0, uv).rgb;
   	vec2 offset = vec2(1.0 / iResolution.x, 0.0);
    for(float i = 1.5; i <= R; i+=2.0){
    	sum_c += texture(iChannel0, uv + offset * i).rgb * 2.0;
        sum_c += texture(iChannel0, uv - offset * i).rgb * 2.0;
    }
    fragColor = vec4(sum_c / (2.0 * R + 1.0), 1.0);
}

