void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv      = fragCoord.xy / iResolution.xy;
    float p      = tanh(texture(iChannel0, uv).y/(float(iFrame)/128.0));
    float period = texture(iChannel0, uv).z;
    fragColor    = vec4(p*uv.y,p*uv.x,p*period,1.0);
}

float threshold = 0.001;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    float r = mix(2.5,4.0,sqrt(uv.x));


    for(int i = 0; i < 60; i++)
    {
        float val  = texture(iChannel0,uv).x;
        val = val == 0.0 ? 0.5 : val;

        float acc      = texture(iChannel0,uv).y;
        float period   = texture(iChannel0,uv).z;
        float last_hit = texture(iChannel0,uv).a;

        fragColor  = vec4(r*val*(1.0 - val), acc, period, 1.0);

        if(abs(fragColor.x - uv.y) < threshold)
        {
        	fragColor.y += 1.0;
            fragColor.z = tanh(iTime - last_hit);
            fragColor.a = iTime;
        }

    }

}
