float RayMarch(vec3 ro, vec3 rd) 
{

    // The extra distance might force a near-plane hit, so
    // it's set back to zero.
    float dO = 0.; 
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);

        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;        
        dO += dS;
    }

    return dO;
}




