vec3 sph2cart(vec3 a) // spherical radians theta, psi, radius -> cartesian
{
    float cx = cos(a.x), sx = sin(a.x)
        , cy = cos(a.y), sy = sin(a.y);
    return vec3(sx*cy, sy, cx*cy) * a.z;
}

