#define shiftr(a,b) a / int(pow(2.,float(b)))   

int imax(int a, int b){
    return a>b?a:b;
}
int modi(int a, int n){
    return a - (n * int(a/n));
}
int p2(int x){
    return int(pow(2.,float(x)));
}
int absi(int x){
    return x < 0? -x : x; 
}

int clampi(int x, int a, int b){
    x = (x < a ? a : x);
    x = (x > b ? b : x);
    return x; 
}

bool checkin(int x, int y, int w, int h){
    return x>=0&&x<w && y>=0&&y<h; 
}

