float ch(ivec2 p, int ch) {
    float c = 0.;
    if (checkin(p.x,p.y,8,32)) {
        for (int i = 0; i < CHARS_LEN; i += 1) {
            if (i == ch)
                for (int yi = 0; yi < 24; yi += 1) {
                    if (-p.y+12 == yi){
                        c += float(modi((CHARS[i][3 - yi / 4] /p2 (modi(yi , 4) * 8) /p2( modi(absi(p.x),8))),2))/1.;
                    }
                }
        }
    }
    return c;
}

