int getTextChAt(int at){
    ivec4 vt = ivec4(0);  
    int s1 = 0;
    int s2 = 0;

    for (int i = 0; i < TEXT_LEN; i++)   {
        if (( at / 16) == i) {
            vt = (TEXT[i]);
            break;
        }
    }

    int b = modi(at/4,4);
    for (int i = 0; i < 4; i++) {
        if ( i == b) {
            s1 = vt[i]; 
            break;
        } 
    }
    return modi(shiftr(s1, 24-modi(at, 4)*8),256);

}

