    for (int i=0; i<256; i++) {
        if (keyDown(i) && i >= START_CH && i<=122) {
            key = (i-START_CH); 
            break;
        }
    }
    return key;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 ir = ivec2(resolution); 
    ivec2 ip = ivec2(fragCoord.xy);
    vec2 fp = vec2(ip);
    vec2 fr = vec2(ir);
    vec2 uv = fp/fr;
    int row = ir.x/8;

    // load memory //
    for (int i = 0 ; i < len; i++) 
        m[i] = texelFetch(iChannel0, ivec2(i,0),0)*255.;

    ivec2 spg = ivec2(ip.x/8-1,ir.y/16-ip.y/16-1);// screen point
    ivec2 locScreen = loc(spg);
    ivec4 cursor = ivec4(m[0]);
    ivec4 print_mem_start = ivec4(m[1]);
    ivec4 print_mem_end   = ivec4(m[2]);
    ivec4 print_mem_stack = ivec4(m[3]);
    ivec4 print_mem_int   = ivec4(m[4]);
    ivec4 print_mem_time  = ivec4(m[5]);
    ivec4 print_mem_prog  = ivec4(m[6]);    


    loadInt(print_mem_start);
    loadInt(print_mem_end);
    loadInt(print_mem_stack);
    loadInt(print_mem_int);
    loadInt(print_mem_time);
    loadInt(print_mem_prog);

    ivec4 tch = getCharCursor(); 
    ivec4 sch = getCharCursor(); 
    if (locScreen.y==0)
        tch *= 0;

    if (ip.y<LINES+2)
    {
        setup_text();

        fragColor = texelFetch(iChannel0, ip, 0); // mem last state

        ivec2 sp = cursor.xy;
        setCursor(cursor.xy);
        tch = getCharCursor(); 

        if (print_mem_time.x <= int(iTime))
        {
            if (cursor.y > LINES){
                // scroll lines
                cursor.y -= 1;
                int t = (ip.x+ip.y*80) / (ir.x);
                fragColor = texelFetch(iChannel0, ip+ivec2(0,1), 0);
            } 
            else if ((print_mem_start.x) < (print_mem_end.x) ) {

                for (int i = 0; i < MAX_PRINT_SPEED; i++) { 

                    if (i > cursor.z + PRINT_SPEED) // cursor.z => max char for frame 
                        break;

                    if ( (print_mem_start.x) < (print_mem_end.x) ) {

                        // print text
                        tch.x = getTextChAt(print_mem_start.x);
                        setCursor(cursor.xy);
                        cursor.x += 1; 
                        print_mem_start.x += 1;

                        if (tch.x == 1) {
                            // \n
                            cursor.x = 0;
                            cursor.y += 1;                          
                        }
                        if (tch.x == 0)
                        {
                            // end string
                            print_mem_start.x = print_mem_end.x;                
                            print_mem_prog.x = print_mem_stack.x;
                            cursor.y += 1;
                            cursor.x = print_mem_stack.x > 0 ? cursor.x : 0;
                            break;
                        }

                        if (cursor.y > LINES)
                            break;

                        writeChOnCursor();
                    }
                }
            }
            else
            {
                if (cursor == ivec4(0.0) ){
                    // start program
                    if (KEYBOARD==0.){
                        printTo(START,30);
                    }
                    else{
                        printTo(START, 0);
                    }

                }
                else if (sp.x == 0){
                    // if sp (screen position <=> terminal cursor) == 0 then print drive
                    print(DRIVE);
                }

                if (keyDown(ESC) && print_mem_prog.x > 0){
                    // if ESC back terminal and clean screen
                    print_mem_prog.x = 0;
                    fragColor *= 0.;
                    cursor.x = 0; 
                    cursor.y = 1; 
                }
                else if (keyDown(LEFT)){
                    cursor.x -= 1; 
                }
                else if (keyDown(RIGTH)){
                    cursor.x += 1; 
                }
                else if (keyDown(UP)){
                    //cursor.y += 1; 
                }
                else if (keyDown(DOWN)){
                    //cursor.y -= 1; 
                }
                else if (keyDown(ENTER))
                {

                    ivec4 inputCh;
                    ivec4 inputCh2;
                    ivec4 ocursor = cursor;
                    for (int i = 0; i < 4; i++) {
                        ivec2 p1 = cursor.xy-ivec2(i+1,0);
                        setCursor(p1);
                        inputCh[3-i] = getCharCursor().x ;
                        p1 = ivec2(i+4,cursor.y);
                        setCursor(p1);
                        inputCh2[i] = getCharCursor().x ;
                    }
                    setCursor(ocursor.xy);

                    if (inputCh.yzw == ivec3(D,I,R) || inputCh2.xyz == ivec3(D,I,R))
                    {
                        cursor.x = 1; 
                        cursor.y += 1; 
                        print(DIR);
                    }
                    else if (inputCh2.xy == ivec2(C,D))
                    {
                        cursor.x = 0; 
                        cursor.y += 1; 
                        print(CD);
                    }
                    else if (inputCh2.xy == ivec2(M,D)  || inputCh2.xyz ==  ivec3(D,E,L) || inputCh2.xyzw == ivec4(A,T,T,R))
                    {
                        cursor.x = 0; 
                        cursor.y += 1; 
                        print(CD_NEG);
                    }
                    else if (inputCh.yzw == ivec3(C,L,S))
                    {
                        fragColor *= 0.;
                        cursor.x = 0; 
                        cursor.y = 1; 
                    }
                    else  if (inputCh.xyzw == ivec4(I,R,U,S))
                    {
                        cursor.x = 0; 
                        cursor.y = LINES; 
                        printTo(LOADING,2);
                    }
                    else  if (inputCh2.xyzw == ivec4(B,A,L,L))
                    {
                        cursor.x = 0; 
                        cursor.y += 1; 
                        printTo(BALL,10);
                    }
                    else  if (inputCh2.xyzw == ivec4(S,H,A,D))
                    {
                        cursor.x = 1; 
                        cursor.y += 1; 
                        printTo(SHADERTOY,20);
                    }
                    else  if (inputCh2.xyzw == ivec4(R,O,D,R))
                    {
                        cursor.x = 1; 
                        cursor.y += 1; 
                        printTo(SHADERTOY,50);
                    }
                    else  if (inputCh2.xyzw == ivec4(G,L,S,L))
                    {
                        cursor.x = 1; 
                        cursor.y += 1; 
                        printTo(LOADING,32);
                    }
                    else if (inputCh2.x != 0 || inputCh2.y != 0 || inputCh2.z != 0)
                    {
                        cursor.x = 0; 
                        cursor.y += 1; 
                        print(FAIL);
                    }
                    else
                    {
                        cursor.x = 0; 
                        cursor.y += 1; 
                    }

                }
                else if (keyDown(32))
                {
                    cursor.x += 1; 
                }
                else if (keyDown(8) && sp.x > 4)
                {
                    cursor.x -= 1; 
                    setCursor(cursor.xy);
                    tch.x = (0); 
                }
                else
                {
                    int ch = getKeyChar();
                    if (ch>0){
                        tch.x = ch; 
                        cursor.x += 1;
                    }
                }

                vec2 fv = vec2(float(modi(int(ip.x),90)+13), float(modi(int(ip.y),80)+18))*vec2(1.,2.)-vec2(52. ,55.);
                float t = fract(time*.1)*320.;
                if (print_mem_prog.x == 1)
                {
                    int cov  = int((fract(0.7325*iTime*0.01)*2.+1.)*(fp.x*fp.y+fp.x+fp.y));
                    fragColor.xyz = vec3(float(modi(cov,50))/255.);
                }
                else if (print_mem_prog.x == 2)
                {
                    delaySec(1.0);
                    tch.x = tch.x == (45-START_CH) ? (124-START_CH ) : (45-START_CH); 
                        if (time>=19.5)
                        {
                            printTo(LOADING, 1);
                        }
                }
                else if (print_mem_prog.x == 10)
                {
                    delaySec(3.0);
                    printTo(LOADING, 11);
                }
                else if (print_mem_prog.x == 20)
                {
                    delaySec(1.0);
                    print_mem_prog.x = 0;
                    cursor.x = 0; 
                }
                else if (print_mem_prog.x == 30)
                {
                    printTo(SHADERTOY, 31);
                }  
                else if (print_mem_prog.x == 31)
                {
                    printTo(DIR, 0);
                }
                else if (print_mem_prog.x == 32)
                {
                    fragColor.xyz = (vec3(clamp((t-length(fv))/700.,0.,1.))) ;
                }  
                else if (print_mem_prog.x == 50)
                {
                    ivec3 tOut = ivec3(C,A,L);
                    for (int i = 0; i < 3; i++)
                        if (ivec2(fv)==ivec2(0+i,1))
                            fragColor.xyz = vec3(float(tOut[i]+1)/256.) ;
                        }
            }
        }

        saveInt(print_mem_start);
        saveInt(print_mem_end);
        saveInt(print_mem_stack);
        saveInt(print_mem_int);
        saveInt(print_mem_time);
        saveInt(print_mem_prog);

        m[0] = vec4 ( cursor );
        m[1] = vec4 ( print_mem_start );
        m[2] = vec4 ( print_mem_end );
        m[3] = vec4 ( print_mem_stack );
        m[4] = vec4 ( print_mem_int );
        m[5] = vec4 ( print_mem_time );
        m[6] = vec4 ( print_mem_prog );

        for (int i = 0 ; i < len; i++) {
            if (ip == ivec2(i,0)){
                fragColor =	m[i]/255.; 
            }
        }


        writeChOnCursor();

        fragColor.a = 1.; 
        return;
    }

    setup_font();

    vec3 cor = vec3 (0.);
    float color = 1.0;
    float c = 0.;

    float t = iTime;
    float td = abs(fract(t*.2)*2.-1.);
    td*=td;
    td*=td;
    if (print_mem_prog.x == 1)
        ip.x += int(cos(fp.y/20.+iTime*15.)*2.*td);


    if (modi(int(time*4.),2)==0)
        c += ch(ip-cursor.xy*ivec2(8,-16)-ivec2(8,ir.y-25),95-START_CH);

    if (tch.x!=0 && spg.y>=0 && spg.x<80 && spg.y<=LINES){
        ivec2 it = ip;         
        c += ch(ivec2(modi(it.x,8),modi(it.y+int(0.),16)),tch.x);       


        if (print_mem_prog.x == 1)
        {

            float cm = 0.;
            ivec2 it = (ivec2(ip.x,ip.y-ir.y+10))/16-ivec2(1,-17)-ivec2(+40-modi(int(iTime*10.),100),0);
            int i = 0;
            #define _(v)  cm += ch(ivec2(it-ivec2(i++*8,0)),v);
            _(C)_(O)_(V)_(I)_(D)_(19)_(27)
                c *= 1.-min(.4,cm);

            if (c <= 0.1)
                c = abs(cos(fp.y*2.))*.1;
            else
                c -= abs(cos(fp.y*2.))*.5;

        }

    }
    cor+=c*.8;
    vec2 pp = vec2(ivec2(fp)/4)*4. ;
    float tf = float(int(iTime*20.))/60.;
    vec2 pb =  vec2(abs(fract(tf*.1375)*2.-1.),abs(fract(tf*0.8)*2.-1.))*vec2(640.,360.)   ;
    pb =  vec2(pb.x, fr.y - pb.y);
    vec2 pfb = pp-pb;

    if (print_mem_prog.x == 11)
    {
        cor = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))*.2;
        cor += vec3(clamp(sin(max(0.,50.0-(length(pfb)))/5.), 0., 1.));
        cor += vec3(ivec3(cor*16.))/16. ;
    }
    if (print_mem_prog.x == 50)
    {
        vec3 b;
        b += vec3(clamp(sin(max(0.,50.0-(length(pfb)))/5.), 0., 1.));
        b += vec3(ivec3(b*16.))/16. ;
        if (b.x > .0)
        {
            cor += b;
        }
    }

    fragColor = vec4( vec3( cor ), 1.0 );

}

