int modi(int a, int n){
    return a - (n * int(a/n));
}

vec2 mainSound( in int samp, float time )
{

    float fb = 71.;
    float f2 = 435.711;
    vec2 s ;
    // time += 30.;
    //HD

    bool b = modi(int(time*fb),2)==0;
    if (b)
    {
        if ( time<20. )
            for (float i = 0.; i < 50.; i++)
            {
                if (b && modi(int(time*7.), int(fract(.235711*(time*10.+5.))*10.))==0 && (time) >=0. )
                {
                    float fr = (fract(.435711*time)*1.-1.1*fract(.935711*time)); // 2.83
                    fr = 2.83;
                    s += vec2( pow(fract(fr*f2*time), i*5.) );
                }
            }
    }

    // Siren
    time-=20.;
    if ( time>0. )
    {
        for (float i = 0.; i < 50.; i++)
        {
            {
                float fr = fract(.235711*time)*1.*1.83; // 2.83
                s += vec2( pow(fract(fr*f2*time), i*5.) );
            }
        }
        s *=.1;
    }

    if (b)
    {
        // Music
        /**/
        time-=10.;
        if ( int(time*7.)%4==0 && (time) >=0. )
        {
            s += vec2( pow(fract(2.83*f2*time),10.) );
        }
        if (int(time*7.)%3==0 && (time) >=5. )
        {
            s += vec2( pow(fract(0.31*f2*time),10.) );
        }
        if (int(time*7.)%2==0 && (time) >=16. )
        {
            s += vec2( pow(fract(1.00*f2*time),10.) );
        }
        if ( int(time*7.)%7==0 && (time) >=20. )
        {
            s += vec2( pow(fract(1.91*f2*time),10.) );
        }
        if ( int(time*7.)%11==0 && (time) >=30. )
        {
            s += vec2( pow(fract(1.22*f2*time),10.) );
        }
        if ( int(time*3.)%13==0 && (time) >=40. )
        {
            s += vec2( pow(fract(2.14*f2*time),10.) );
        }
        if ( int(time*2.)%17==0 && (time) >=50. )
        {
            s += vec2( pow(fract(3.52*f2*time),10.) );
        } 
        if ( int(time*1.)%19==0 && (time) >=60. )
        {
            s += vec2( pow(fract(3.06*f2*time),10.) );
        }
        if ( (time) >= 70. && (time) <= 75. )
        {
            s = vec2( (fract(2.06*f2*pow(time/2.,(70.-time*1.)/2.)) ) );
        }
        if ( (time) >= 75. && (time) <= 80. )
        {
            s = vec2( pow(fract(3.06*f2*(70./time)),10.) );
        }

        /**/
    } 

    if ((time) >= 80. )
    {
        s = vec2( 0. );
    }
    return s*.05;
}
