void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 orig = textureLod(iChannel0, uv, 0.0);
    vec4 buff = textureLod(iChannel0, uv, sqrt(iResolution.x/250.));

    fragColor = orig *.5 + buff*.5;
}

#define SIZE 25.

#define R_FACTOR .8

#define BLACK_COL vec3(41,55,66)/255.
#define WHITE_COL vec3(245,248,250)/255.

float rand(vec2 co) { 
    return fract(sin(dot(co.xy , vec2(12.9898, 78.233))) * 43758.5453);
} 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 cuv = (fragCoord.xy - .5*iResolution.xy)/iResolution.y;         

    float t = iTime*.1;
    float ac = cos(t);
    float as = sin(t);
    mat2 rot = mat2(ac,-as,as,ac);
    cuv *= rot;

    // Fish eye
    float fovTheta = 7.55;    
    float z = sqrt(0.5 - cuv.x * cuv.x - cuv.y * cuv.y);
    float a = 1. / (z * tan(fovTheta * 0.5));	
    vec2 uv = cuv * a;

    vec2 ruv = uv*SIZE;    
    vec2 id = ceil(ruv);    

    ruv.x -= iTime*5. * (rand(vec2(id.y))*0.5+.5); // move right    
    vec2 guv = fract(ruv) - .5; 

    id = ceil(ruv);    

    float m = step(rand(id), R_FACTOR);
    m *= ceil(mod(id.y, 2.)); // remove every second    

    for(float i = -1.; i<=1.; i+=2.){
        float mn = step(rand(id + vec2(i, 0.)), R_FACTOR);

        float l = length(guv + vec2(.5,0.)*i) * (1.-mn);
        m *= step(l, .5);        

    }

    // Big circle
    float ll = length(cuv);
    m *= step(ll, .5);

    vec3 col = mix(WHITE_COL, BLACK_COL, clamp(m, 0., 1.));

    fragColor = vec4(col,1.0)*.2 + texture(iChannel0, ouv)*.8;

}
