void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xy;

	float right = step(1.8 * uv.x - 0.8, uv.y);
//	float right = smoothstep(2.0 * uv.x - 1.01, 2.0 * uv.x - 0.99, uv.y);

    float left = step(-1.8 * uv.x + 1.0, uv.y);
//  float left = step(-2.0 * uv.x - 1.0, uv.y);
	vec4 color = vec4(left) * vec4(right);
	fragColor = color;

}
