#define pi 3.1415926535
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = texture(iChannel0,fragCoord.xy/iResolution.xy).xy;
    //z = 2.*(z-0.5);
    fragColor = vec4(length(z)/2.,1.-0.5*length(z),atan(z.x,z.y)/(2.*pi)+0.5,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 c = fragCoord.xy/iResolution.x;

    c-= vec2(0.5,0.5*iResolution.y/iResolution.x);
    //Zoom (multiply by smaller number to zoom in)
    c*= 0.0006;
    //Change center c value
    c += vec2(0.2699,0.0049);
    if (iFrame % 1000 == 0){
        fragColor = vec4(0,0,0,1);
        return;
    }
    vec2 z = texture(iChannel0,uv).xy;
    //z = 2.*(z - 0.5);
    if (iFrame % 2 == 0){
 		if (dot(z,z) < 4.)
    		z = vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y) + c;
    }
    fragColor = vec4(z,0,0);
}
