#define hash Nth_weyl   // Nth_weyl or Uniform

vec2 Nth_weyl(int n) { // borrowed from https://www.shadertoy.com/view/3lsXW2
    return fract(vec2(n*12664745, n*9560333)/exp2(24.));
}

#define Uniform(p)  fract(sin(float(p) * vec2(12.9898, 78.233)) * 43758.5453)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, D;
    U = U/R.y; U.x -= (R.x/R.y-1.)/2.;
    float t = 50.*iTime, m=1e9, ii;

    for (int i = int(t); i > 0; i--) {
        D = hash(i) - U; 
        m = dot(D,D); if(m*R.y*R.y/6.<2.) { ii=float(i);break; }
     // O += smoothstep(3.5,2.,length(D)*R.y);
     // O += clamp(2.-dot(D,D)*R.y*R.y/6.,0.,1.) 
     //      * min(float(i)/t * vec4(1.4,1,.8,0),1.);
    }
    O = clamp(2.-m*R.y*R.y/6.,0.,1.) 
        * min(float(ii)/t * vec4(1.4,1,.8,0),1.);
}
