float random (float x)
{
    return fract(sin(dot(vec2(x,x),vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float seed = iMouse.x;

    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    vec3 c = vec3(0.);

    for(float i = 0.0;i < 8.0;++i)
	{
    	float check = abs(floor(fract((st.x+st.y)*pow(2.0,i))*2.)-floor(fract((st.x-st.y)*pow(2.0,i))*2.));
        vec3 colors = vec3(random(123.0*(i+1.0)+check+seed),random(573.0*(i+1.0)+check+seed),random(778.0*(i+1.0)+check+seed));

        c = abs(c - fract(colors*(iTime*.4)));
    }

    c*=5.;
    c-=3.3;

    c = sin(c);

    fragColor = vec4(c,1.0);
}
