#define PI           3.141593
#define rot(a)       mat2(cos(a+vec4(0,11,33,0)))
#define lin(x)       .5+.5*sin(30.*(x))
#define osc(u,v)     lin(P.x+u*sin(v*2.*PI*P.y))
#define spi(a,u,v) ( P = Q*rot(PI*a) , osc(u,v)  )

void mainImage( out vec4 O, vec2 u )
{
    O-=O;
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y,
      // M = iMouse.xy/R,
         Q = vec2( atan(U.y,U.x), log2(length(U)) /* -iTime */ ),  P;

    O.r = spi(.117, .05,1.28);
    O.g = spi(.333, .1 ,.912);

    O =   pow(O,vec4(16.*dot(U,U))) 
        * smoothstep(.2,-.4,Q.y);

}
