// Fork of "Water Fountain" by noobay. https://shadertoy.com/view/tllcWl
// 2020-06-29 20:47:47


// noise and fbm taken from https://iquilezles.org/articles/fbml, https://www.shadertoy.com/view/Msf3WH
// simplex, fbm and parabola SDF by - Inigo Quilez

vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

float fbm( in vec2 x, in float H, in int numOctaves )
{    
    float t = 0.0;
    for( int i=0; i<numOctaves; i++ )
    {
        float f = pow( 2.0, float(i) );
        float a = pow( f, -H );
        t += a*noise(f*x);
    }
    return t;
}

float sdParabola( in vec2 pos, in float k )
{    
    pos.x = abs(pos.x);

    float p = (1.0-2.0*k*pos.y)/(6.0*k*k);
    float q = -abs(pos.x)/(4.0*k*k);

    float h = q*q + p*p*p;
    float r = sqrt(abs(h));

    float x = (h>0.0) ? 
        pow(-q+r,1.0/3.0) - pow(abs(-q-r),1.0/3.0)*sign(q+r) :
        2.0*cos(atan(r,-q)/3.0)*sqrt(-p);

    return length(pos-vec2(x,k*x*x)) * sign(pos.x-x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) /iResolution.y;

    float parabola = sdParabola(uv, 100.0) * 7.0;

    parabola *= (sdParabola(-uv, 2.0) * 1.3);

    vec2 fbmUv = vec2(uv.x, uv.y + iTime);

    float fbmResult = fbm(fbmUv, 0.55, 3);

    vec3 col = mix(fbmResult * vec3(1.0, 1.0, 1.0), parabola * vec3(1.0, 1.0, 1.0), parabola * vec3(1.5));
    // Output to screen
    fragColor = vec4(col,1.0);
}
