// looplessing julianlumia's "Expanding Fibonacci Flower" https://shadertoy.com/view/wllyzH

void mainImage(out vec4 O, vec2 u) {            
    vec2  R = iResolution.xy,                               
          U = u+u-R, A = abs(U);                            
    float m = 1e6, 
          r = max(A.x, A.y)*.06,                       // disk radius
          X = floor(sin(iTime)*95.+95.),               // smaller disk index
          D = 24. * r/R.y/.06,                         // spiral strips window (disk coverage)
         PI = 3.1415927,
          a = atan(-U.y,U.x), l = length(U)/R.y*15., 
         x1 = ( a -iTime/2.) / 2.307, L = 2.*PI/2.307,
         x2 = l*l +  X,
          k = round((x2-x1)/L),
          x = round( x1 + L*k);                        // disk center
    if (x>=X) D = 2. + D*sqrt( x - X ) / sqrt(2e2);    // update strip window according to local spiral width
    O-=O; 
    if (iMouse.z>0.) O += U.y > 0. ? fract(x1+k*L)       // top:    show underlying spirals
                                   : floor(2.*D/2.3)/23.;// bottom: show strip window size: white=22
    for( float d = -D; d <= D; d+=2.307)  { 
        k = round((x2+d-x1)/L), x = round( x1 + L*k);
        if (x>=X && x<=2e2 )
      //for (x=0.; x++<2e2;) {                  // initial loop on disks
        m = min( m,  length( U - R.y*sqrt( x - X ) / 15.
                                 *cos (x * 2.307 + iTime/2. + vec2(0,PI/2.))) ) ;  
       }
    O += vec4(0,1,abs(U/R).yy) * clamp(.5-(m-r)/3., 0.,1. );
}
