vec3[] colors = vec3[](
    vec3(255.,   0.,   0.) / 255.,
    vec3(255., 127.,   0.) / 255.,
    vec3(255., 255.,   0.) / 255.,
    vec3(  0., 255.,   0.) / 255.,
    vec3(  0.,   0., 255.) / 255.,
    vec3( 46.,  43.,  95.) / 255.,
    vec3(139.,   0., 255.) / 255.
);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float len = float(colors.length());
    float h = 1. / len;
    int i = int(floor(uv.y / h));
    float j = fract(uv.y / h);

    int next = int(mod(float(i+1), len));
    vec3 col = mix(colors[i], colors[next], j);

    fragColor = vec4(col, 1.0);
}
