vec3 calc( in vec2 p )
{
    float x = 0.4;
	float h = 0.5;
	for( int i=0; i<11; i++ )
	{

  x = p.x+p.y*p.y+2.*p.x*p.y*(1.+sin(iTime+p.x)) *p.x/x
     +p.x*sin(1.+x+sin(iTime+x*1.1+p.y*1.+p.x));
      h += log2(abs(p.y*(1.0-4.0*x+sin(iTime))));


	}
    h /= 35.0;


	h = abs(h);
	vec3 col = 1.5*( vec3(2.5, 1.0, 0.3) + h );

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec3 col = calc( vec2(-4.4,-2.5) + 9.0*fragCoord/ iResolution.x )/5.;
 fragColor = vec4( col, 1.0 );
}
