void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 vid0 = texture(iChannel0, uv).xyz;
    vec3 vid1 = texture(iChannel1, uv).xyz;

    // Output to screen
    vec4 bg = texture(iChannel2, vec3(uv, 1))*texture(iChannel2, vec3(uv, 1));
    fragColor = vec4(reflect(vid1*vid0*vid1, vec3(0,1,0)),1);
    float temp = fragColor.x;
    fragColor.x = fragColor.y;
    fragColor.y = temp;
    if(fragColor.x+fragColor.y+fragColor.z < 0.07) fragColor = bg;
}
