#define L 128

float foo(float y, float r)
{
	return (r * y) * (1. - y);
}

float bifurcation(float y, float r, int it)
{
	for (int i = 0; i < it; ++i) {
        y = foo(y, r);
    }
    return y;
}

float[L] bifurcation(float a, float r)
{
    float y[L];
    y[0] = a;
    for (int i = 1; i < L; ++i) {
    	y[i] = foo(y[i - 1], r);
    }
    return y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 c = vec3(0);

    // animated
    float p = 1. - abs(pow(mod(iTime * .05, 2.) - 1., 3.));
    // mouse
    //float p = m.x;

    // position
    c.b += smoothstep(.005, .0, abs(dot(uv - vec2(p, 0), vec2(1, 0))));

    // f(x)
    c.r += smoothstep(.01, .0, abs(uv.y - bifurcation(.5, p * 4., int(fragCoord.x))));

    // what f(x) 'converges' towards
    float b[L] = bifurcation(.5, uv.x * 4.);
    for (int i = L / 2; i < L; ++i) {
    	c.g += .3 * smoothstep(.005, .0, abs(uv.y - b[i]));
    }

    fragColor = vec4(c, 1.);
}
