void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv =  fragCoord.xy/iResolution.xy;
    vec2 uvx =  uv *3.0;
    float t   = iTime;

    float t2 = t*3.0;

    float d =   1.-smoothstep(0.1,1.5+sin(t2)-0.5,length(uvx-1.5-vec2(0.+sin(t2*0.2),0.+cos(t2*0.2))));

    float c =    smoothstep(0.1,1.5,length(uvx-1.0*vec2(1.0-sin(t2*0.3),0.8-cos(t2*0.3))));  

   // uv.x/=1.02;

    vec3 tx,tx2 = vec3(0.0);

    for (int i=0;i<20;i++){

        float j= float(i);

         tx   +=  texture(iChannel0,uv-vec2((j*0.001*sin(t*t)),0.0)).rgb *j/190. ;



         tx2 += texture(iChannel0,uv).xyz*j*0.02;

    }


    float a,b,final  = 0.0;

    if (t2>20.0) {

           a = gg(uvx*2.-1.-vec2(0.0+2.*cos(t2),2.5+2.*sin(t2)))+
               ii(uvx*2.-1.-vec2(0.5+2.*cos(t2-0.5),2.0+2.*sin(t2-0.5)))+
               gg(uvx*2.-1.-vec2(1.0+2.*cos(t2-1.0),2.0+2.*sin(t2-1.0)))+
               aa(uvx*2.-1.-vec2(1.5+2.*cos(t2-1.5),2.0+2.*sin(t2-1.5)))+
               tt(uvx*2.-1.-vec2(2.0+2.*cos(t2-2.0),2.0+2.*sin(t2-2.0)))+
               rr(uvx*2.-1.-vec2(2.5+2.*cos(t2-2.5),2.0+2.*sin(t2-2.5)))+
               oo(uvx*2.-1.-vec2(3.0+2.*cos(t2-3.0),2.0+2.*sin(t2-3.0)))+
               nn(uvx*2.-1.-vec2(3.5+2.*cos(t2-3.5),2.0+2.*sin(t2-3.5)));



    }





    if (t2>10.0) {

           b  +=   shdemo(2.*uvx*2.-1.-vec2(-0.0,-1.0))-d*0.8  ;


    } 


    if (t2>2.0) {

          final =  exp(9.*a+texture(iChannel3,uvx).r+2.*abs(sin(8.*c+0.8*t2+b*8.5)))/16.0;

    } 






    fragColor = vec4((tx*tx2) ,1.0)*final ;



}
