// COMMON
#define t(uv) textureLod(ch, uv, .5).xy
#define rnd(x) fract(1e5*sin(1e5*x))
#define fl(x) floor(x*10.)/10.
//#define R (3.+15.*smoothstep(10.,0.,t))
#define R 5.
#define s(x) smoothstep(0.,1.,x)

#define K (.06)
#define F (.05)
#define Da 1.*s(t)
#define Db .4*s(t)

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec4 reaction(vec2 FC, vec2 r, sampler2D ch, float t, vec4 m)
{
    vec2 uv=FC/r,_,px=1./r,v=t(uv),dx,dy,d,delta,dir;

    // flow
    //uv=((uv-.5)*rot(.05*length(uv-.5))+.5);

    //vec2(R,0.) will also work 
    _=vec2(R+R*rnd(uv.x+.01*uv.y+t),0.);

    //uv=abs(uv-.5);

    dx=(-v+(t(uv+px*_   )+t(uv+px*-_   ))/2.)/2.;
    dy=(-v+(t(uv+px*_.yx)+t(uv+px*-_.yx))/2.)/2.;

    //dir=vec2(1,0)*rot(t/10.)*.5+.5;
    d=(dx+dy)/2.;

    delta.x=Da*d.x-v.x*v.y*v.y+F*(1.-v.x);
    delta.y=Db*d.y+v.x*v.y*v.y-(K+F)*v.y;
    vec4 o=vec4(v+delta,1,1);

    if(t<.1)o*=0.,o+=1.-step(.05,length(uv-.5));//o+=rnd(fl(uv.x)+.01*fl(uv.y));

    return o;
}

void mainImage( out vec4 o, in vec2 FC )
{
    o = reaction(FC.xy, iResolution.xy, iChannel0, iTime, iMouse);
}

