void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 d = D(U);
    vec4 c = C(U);
    float l = ln(U,a.xy,a.zw);
    Q = (0.3*c*vec4(1.5,.94,1.05,1)-.3)*exp(-2.*l*l);

}

#define R iResolution.xy
#define o vec3(1,0,-1)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, vec2 U)
float ln (vec2 p, vec2 a, vec2 b) {
	return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
#define norm(u) ((u)/(1e-9+length(u)))

// Dave
vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}

void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
}
Main {
    Q = A(U);
    for (int x = -1;x <=1; x++)
    for (int y = -1;y <=1; y++)
    X(Q,U,vec2(x,y));
    vec4 c = C(Q.xy);
    Q.xy = mix(Q.xy,A(Q.xy).xy,.05+.05*sin(c.x));
    Q.zw = mix(Q.zw,A(Q.zw).zw,.9);
    Q.xy += D(Q.xy).xy;
    Q.zw += D(Q.zw).xy;

    if (length(Q.xy-Q.zw) > 4.+2.*sin(c.w)) {
        vec2 m = 0.5*(Q.xy+Q.zw);
        if (length(U-Q.xy) > length(U-Q.zw)) 
        	Q.xy = m;
        else Q.zw = m;
    }
    if (iMouse.z>0.) {
        vec4 n = B(vec2(0));
    	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
    }
    if (iFrame<1) {
        Q.xy = U;
        Q.zw = 0.5*R;
    }

}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
}
Main {
    Q = A(U);


    if (length(Q.xy-Q.zw) > 2.5) {
        vec2 m = 0.5*(Q.xy+Q.zw);
        if (length(U-Q.xy) > length(U-Q.zw)) 
        	Q.xy = m;
        else Q.zw = m;
    }

    Q = vec4(C(Q.xy).xy,C(Q.zw).zw);

    if (iFrame < 1) {
        Q = hash42(U+iDate.zw)*6.3;
    }
}

vec4 T(vec2 U) {
	U -= .5*D(U).xy;
	U -= .5*D(U).xy;
    return D(U);
}
Main {
    Q = T(U);
    vec4 
        n = T(U+o.yx),
        e = T(U+o.xy),
        s = T(U+o.yz),
        w = T(U+o.zy),
        m = 0.25*(n+e+s+w);
    Q.xy = m.xy-0.25*vec2(e.z-w.z,n.z-s.z);
	Q.z = Q.z-0.25*(n.y+e.x-s.y-w.x);
    vec4 a = A(U), c = C(U);
    float l = ln(U,a.xy,a.zw);
    float v = smoothstep(1.,0.,l);
    Q.z += 0.01*sin(c.x);
    Q.xy = mix(Q.xy,norm(a.xy-a.zw),.2*v*(.1+sin(c.y)));
    Q.z *= .9-.1*v*sin(c.w);
    Q.xy *= .95-.1*v*sin(c.z);
    if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.) Q.xy *= 0.;
	if (iFrame < 1) Q = vec4(0,0,0,0);
    if (iMouse.z>0.&&length(U-iMouse.xy)<10.)Q.z+=.2;
}
