void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2. * uv - 1.;
    float zoom = 0.001;
    uv *= 1. + zoom;// zoom in

    float time = iTime / 5.;

    vec2 idx = (uv + 1.) / 2.;
    vec3 col = texture(iChannel0, idx, 0.0).rgb;


    float r = length(uv);
    float theta = mod(atan(uv.y, uv.x) + 6.28, 6.28);

    float targett = time * 4.;
    float targetr = 0.4 + 0.2 * sin(time * 10.);
    vec2 target = targetr * vec2(cos(targett), sin(targett));


    float d = length(target - uv);
    col +=
        (d < 0.025 ? 1. : 0.) *
        abs(vec3(sin(time * 3.), sin(time * 7.), sin(time)));
    fragColor = vec4(col,1.0);
}
