void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5/**sin(iTime*.5)*-.5+.5*/)/iResolution.y+.5;
	vec2 gv = vec2(uv.x * 5., /*-.25*/+uv.y * 5.);
    uv /= .5;
    float rad = .125;
    float dist = length(sin(iTime*rad)*gv)-rad;
    // Time varying pixel color
    vec3 col =atan(gv.x*uv.x*.15, gv.y*.1) + 1.618 *cos(iTime+dist+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(pow(col, col),10);//pow(col, col),1.0);
}
