void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //center view around average of system
    vec2 cen = vec2(0,0);
    for(float i = 0.0; i < NUM; i++)
    {

    	vec4 o = texelFetch(iChannel0, ivec2(i,0), 0);
        cen += o.xy;

    }
    cen /= NUM;
    vec2 uv = 2.*(fragCoord-.5*iResolution.xy)/iResolution.y + cen;

    vec3 col = vec3(0);//0.06 + 0.06*cos(iTime+uv.xyx*3.+vec3(0,2,4));

    for(float i = 0.0; i < NUM; i++)
    {

    	vec4 o = texelFetch(iChannel0, ivec2(i,0), 0);

        vec3 c = .5+.5*sin(vec3(1,2,3)*i);

        float d = .005/length(uv-o.xy);

        d = smoothstep(0.2,.4, d);

        col += c*d;

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

//x = xpos
//y = ypos
//z = xvel
//w = yvel

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    if(iFrame <= 3)
    	fragColor = vec4(fragCoord/5.,0.,0.03*sin(fragCoord.x));
    else
    	{
    		vec4 o = texelFetch(iChannel0, ivec2(fragCoord), 0);


            for(float i = 0.0; i < NUM; i++)
            {
                if(int(i) != int(fragCoord.x)){
                    vec4 oo = texelFetch(iChannel0, ivec2(i,0), 0);

                    float dist = distance(o.xy, oo.xy);
                    vec2 direction = -normalize(o.xy-oo.xy);

                    o.zw += GRAV*direction/(dist*dist+DMUL);
                }
            }

            o.x += o.z;
            o.y += o.w;

            //o.x = fract((o.x+1.)/2.)*2.-1.;
            //o.y = fract((o.y+1.)/2.)*2.-1.;

            fragColor = o;
        }
}
