vec4 colWhi = vec4(1.0, 1.0, 1.0, 1.0);
vec4 colCya = vec4(0.0, 1.0, 1.0, 1.0);
vec4 colOra = vec4(1.0, 0.5, 0.0, 1.0);
vec4 colRed = vec4(1.0, 0.5, 0.0, 1.0);

float plot(vec2 coord, float y, float thickness){
    return  smoothstep( y-thickness, y, coord.y)       
    - smoothstep( y, y+thickness, coord.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // =================
    //        BASE
    // =================
    vec2 coord = fragCoord.xy; 
	vec2 xy = coord / iResolution.xy;

	vec2 center = vec2(0.5, 0.5);

    // =================
    //     CALCULUS
    // =================

    //white tube
    float y = sin((coord.x - iTime/20.0)*40.0) / 5.0 + 0.5; 
    float i = cos((coord.x - iTime/20.0)*40.0) + 1.5;
    float movingSine1 = plot(xy, y, 0.01) * i;

    // Cyan clouds
    y = cos((coord.x - iTime/40.0)*90.0)/13.0 + 0.5;
    i = - cos((coord.x + iTime/20.0)*40.0) + 1.5;
    float movingSine2 = plot(xy, y, 0.08) * i;

    // Orange bolts
    y = cos((coord.x + iTime/40.0)*10000.0)/30.0 + 0.5; 
    float movingSine3 = plot(xy, y, 0.01);

    // Orange back
    y = sin((coord.x - iTime/20.0)) / 2.5 + 0.5; 
    i = - cos((coord.x - iTime/20.0)*40.0) * 2.0;
    float movingSine4 = plot(xy, y, 0.01) * i;

	// =================
    //       OUTPUT
    // =================
    fragColor = colWhi * movingSine1 + colCya * movingSine2 + colOra * movingSine3 + colRed * movingSine4;
}
