const float PI = 3.14159265;

// from https://iquilezles.org/articles/distfunctions

float RoundRect( vec2 p, vec2 b, float r )
{
    return length(max(abs(p)-b+r,0.0))-r;
}


float size = 0.35;
float highlight = 0.05;
float stroke = 0.02;
float roundness = 0.02;
float smoothness = 0.01;


vec3 colLow0 = vec3(1, 0, 0);
vec3 colLow1 = vec3(0, 1, 0);

vec3 colHigh0 = vec3(1, 0.95, 0.95);
vec3 colHigh1 = vec3(0.95, 1, 0.95);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_center = uv - 0.5;
    float as = iResolution.x / iResolution.y;
    uv_center.x *= iResolution.x / iResolution.y;

    float rr = RoundRect(uv_center, vec2(size), roundness);

    float inner_low = smoothstep(0., smoothness, rr);
    float outer_low = smoothstep(highlight + stroke * 2. + smoothness * 2., highlight + stroke * 2. + smoothness, rr);

    float inner_high = smoothstep(stroke, stroke + smoothness, rr);
    float outer_high = smoothstep(highlight + stroke + smoothness * 2., highlight + stroke + smoothness, rr);


    float low = inner_low * outer_low;
    float high = inner_high * outer_high;

    float colFactor = sin(uv.y * 5. + iTime * .5) * 0.5 + 0.5;

    vec3 colLow = mix(colLow0, colLow1, colFactor) * low;
    vec3 colHigh = mix(colHigh0, colHigh1, colFactor) * high;

    vec3 col = max(colLow, colHigh);

    float blink = 1. - step(sin(iTime * 2.), -0.9) * (sin(iTime * 50.) * 0.5 + 0.5);

    col = col * blink;

    fragColor = vec4(col, 1.);
}
