void mainImage(out vec4 Q,in vec2 U){
    Q = A(U);
}

float dt = 2.;

#define R iResolution.xy

#define _A(u) texture(iChannel0,(u)/R.xy)
#define _B(u) texture(iChannel1,(u)/R.xy)
#define _C(u) texture(iChannel2,(u)/R.xy)

#define A(id) texelFetch(iChannel0,ivec2(id),0)
#define B(id) texelFetch(iChannel1,ivec2(id),0)


vec2 v(vec2 U, vec2 r){
    vec2 uv = (U*2.-r)/r.y;
    return vec2(-uv.y,uv.x)*6.;
}
vec2 RK4(vec2 U,vec2 r,float dt){
	vec2 k1 = v(U			,r);
    vec2 k2 = v(U-.5*k1*dt	,r);
    vec2 k3 = v(U-.5*k2*dt	,r);
    vec2 k4 = v(U-k3*dt		,r);
	return dt*(k1+2.*(k2+k3)+k4)/6.;
}
float init(vec2 U,vec2 r){
	vec2 uv = U/r.y;
	int X = int(18.*uv.x)%2;
	int Y = int(18.*uv.y)%2;
	return float(X^Y);
}

void mainImage(out vec4 Q,in vec2 U){
    Q = _A(U - RK4(U,R,dt));
    if(iFrame==0)Q = vec4(init(U,R));
}

void mainImage(out vec4 Q,in vec2 U){
    vec4 z = _A(U - RK4(U,R,-dt));
    vec4 k = B(U);
    Q=k+.5*(k-z);
}

void mainImage(out vec4 Q,in vec2 U){
    Q = _A(U - RK4(U,R,dt));
    if(iFrame==0)Q = vec4(init(U,R));
}
