// Rubino Marc - 2021
// https://www.youtube.com/channel/UC4QabYdHqsMLv1WiwtYRzRw
// Optical Illusion

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
    st -= 0.5;
    st.x *= iResolution.x / iResolution.y;
    vec3 cr = vec3(1.,0.,0.);
    float radius = 0.25;
    float dist = length(st);
    float a = smoothstep(dist,dist+0.3,radius);
    float b = step(dist,radius);
    vec4 fc1 = vec4(vec3(cr),1.0);
	vec4 fc2 = vec4(vec3(a)*vec3(1.),1.0);
    vec4 fc3 = vec4(vec3(b)*vec3(cr),1.0);
    fragColor = mix (fc1, fc2, fc3);
}
