#define PI 3.141592
#define BALLS 20.

mat2 rotate2d(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
fragColor = vec4(0);
  vec2 uv = fragCoord / iResolution.xy;
  uv.x -= .5;
  uv.y -= .5;
  uv.x *= iResolution.x / iResolution.y;
  uv *= 116.;
  float dist = length((uv));
  for (float i = 0.; i < BALLS; i++) {
    uv.y += .5 * (i / 4.) * cos(uv.y /444. + iTime / 0.1) + sin(uv.x / 30. - iTime / 0.4);
    uv.x += .5 * (i) * sin(uv.x / 100. + iTime / 1.) * sin(uv.y / 50. + iTime / 4.);
    float t = 0.008 * dist * (i) * PI / BALLS * (5. + 1.);
    vec2 p = 3. * vec2(-1. * cos(t), 1. * sin(t /6.));
    p /= sin(PI * sin(uv.x / 15.) * cos(uv.y / 0.1));
    vec3 col = cos(vec3(6, 6, -1) * PI * 2. / 3. + PI * (iTime / 5. + float(i) / 5.)) * 0.5 + 0.5;
    fragColor += vec4(float(i) * .3 / length(uv - p * 8.3) * col, 0.1);
  }
  fragColor.xyz = pow(fragColor.xyz, vec3(1.5));
  fragColor.w = 6.0;
}
