float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}


vec3 casaCono1(vec3 p)
{ 
   vec3 res;
   res = vec3(9999.0, -1.0,-1.0);

   float sc1= sdCappedCone(  p, 20.0, 10.0, 0.5 );
   float c1= sdCylinderXZ( p-vec3(0.0,15.0,0.0), vec2(5.0,0.25)  );
   float c2= sdCylinderXZ( p-vec3(0.0,12.5,0.0), vec2(5.0,0.25)  );
   float c3= sdCylinderXZ( p-vec3(0.0,10.0,0.0), vec2(5.0,0.25)  );
   float c4= sdCylinderXZ( p-vec3(0.0,7.5,0.0), vec2(5.0,0.25)  );


   res =opU3(res, vec3(sc1,1.0,MATERIAL_NO)); 
   res =opU3(res, vec3(c1,1.0,MATERIAL_NO)); 
