vec3 GetDist(vec3 p  ) 
{
	vec3 res;
    vec3 pp=p;
    res = vec3(9999.0, -1.0,-1.0);

	float planeDist1 = p.y+0.0;  //piso inf

    res =opU3(res, vec3(planeDist1,-1.0,5.0)); //inf
    p.y=p.y-5.0;


    vec3 prepA=p;
    vec3 prepB=p;
    prepA.x =opRep1D( prepA.x, 50.0 );
    prepA.z =opRep1D( prepA.z, 50.0 );

    prepB.x =opRep1D( prepB.x, 100.0 );
    prepB.z =opRep1D( prepB.z, 60.0 );

    vec3 cc1= casaCono1(prepA);
    vec3 cc2= casaCono2(prepB-vec3(20.0,0.0,15.0));

    res =opU3(res, cc1 ); 
    res =opU3(res, cc2 ); 

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro) 
{
    vec3 l = lp - p;
    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    float dif = clamp(dot(l, n), 0.0, 1.0);
    float spe = pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);

    vec3 lin = vec3(0.2);
    lin += 1.0*dif*vec3(1, .97, .85);
    lin += 2.5*spe*vec3(1, .97, .85)*dif;
    lin += 2.5*fre*vec3(1);
    lin += 0.5*dom*vec3(1);

    return lin*atten;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}


float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);

    float marchCount = 0.0;
    vec3 p;


    float minDist = 999.0; 

    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);



         if ( abs(dS.x)<MIN_DIST)
        {
            mObj.hitbln = true; 
            minDist = abs(dO);
            break; 
        }


        if(dO>MAX_DIST)
        { mObj.hitbln = false; 
          minDist = min(minDist, dO);
          break; 
        }

        dO += dS.x;
        marchCount++;

    }

    mObj.dist = dO;
    //mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    return dO;
}




//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
