void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*5.0,500.0);
    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(5.0, 10.0, 5.0 );  light_color1 =1.25*vec3( 1.0 ,0.1,0.1);
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 =1.25* vec3( .1 ,0.1,1.0); //light_color1; //vec3( 1.0, 1.0, 1.0 );


    vec3 ro;
    vec3 rd;

    float t2=mod(iTime,15.0);
    //escena 1
    if(t2<5.0)
    {
        ro=vec3(40.0,60.0,-55.0-t);
        rd=normalize(vec3(uv,1.0));
        rd= rotate_x(rd, radians(90.0) );
    }
    else if (t2<10.0)
    {
         ro=vec3(40.0,5.0,-55.0+t);
         rd=normalize(vec3(uv,1.0));
    }
    else if(t2<15.0)
    {
        ro=vec3(40.0,3.0,-55.0-t);
        rd=normalize(vec3(uv,1.0));
        rd= rotate_x(rd, radians(270.0) );
    }


    light_pos1+=ro;
    light_pos2+=ro;


    vec3 col = vec3(0);
    TObj Obj;
    mObj.rd=rd;
    mObj.ro=ro;

    float d = RayMarch(ro, rd);
    Obj=mObj;
    if(mObj.hitbln) 
    {
        vec3 p = (ro + rd * d ); 
        mObj.p=p;
        mObj.dist =d;
        vec3 nor=mObj.normal;

        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        result/=1.25;
        col= result*dif1;

        //sugerencia por dean_the_coder,
        //col *= 1.0 - pow(d / 100.0, 1.5);
        col *= 1.0 - pow(d /(MAX_DIST) , 3.5);    
        col = pow(col, vec3(1.0/2.2));

    }
    else if(d>MAX_DIST)
     col= getSkyColA(rd);


    fragColor = vec4(col,1.0);
}
