void mainImage( out vec4 O, vec2 u ) {
	vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    float d = 1e3;
    for (float i = 0.; i < 10.; i++ ) {
        float 
             c1 = length( U - vec2(      i /20. ,  i /20. ) ), // distance to point (x,x)  WRONG !
             c2 = length( U - vec2( .1 + i /20. ,  i /20. ) ), // should be the same + slight offset
              x = i/20.,
             c3 = length( U - vec2( .2 + x      ,  x      ) ); // (see below) should be the same + more offset
        d = min(d, min(c1, c2));
     // d = min(d, c3);                                        // uncomment will change c1
    }
    O = vec4( d*100. -5. );


}
